import React, { useState, useEffect } from "react";
import axios from "axios";
import { logout } from "../services/AuthService"; // Giữ lại logout nếu cần
import { useNavigate } from "react-router-dom";
import "./AdminPage.css";

const API_URL = "https://orderfoods2.onrender.com/api/orders";

const AdminPage = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    description: "",
    imageUrl: "",
    quantity: 0,
  });
  const [editingProduct, setEditingProduct] = useState(null);
  const navigate = useNavigate();

  // Lấy danh sách sản phẩm
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(API_URL, {
          headers: { Authorization: token },
        });
        console.log("Dữ liệu sản phẩm:", response.data); // Kiểm tra dữ liệu
        setProducts(response.data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };
    fetchProducts();
  }, []);

  // Hàm thêm sản phẩm
  const handleAddProduct = async () => {
    try {
      if (
        !newProduct.name ||
        !newProduct.price ||
        !newProduct.description ||
        !newProduct.imageUrl ||
        newProduct.quantity < 0
      ) {
        alert("Vui lòng điền đầy đủ thông tin và đảm bảo số lượng là một số hợp lệ");
        return;
      }

      const token = localStorage.getItem("token");
      const response = await axios.post(
        API_URL,
        {
          ...newProduct,
          quantity: Number(newProduct.quantity),
        },
        {
          headers: { "Content-Type": "application/json", Authorization: token },
        }
      );

      setProducts((prevProducts) => [...prevProducts, response.data]);
      setNewProduct({
        name: "",
        price: "",
        description: "",
        imageUrl: "",
        quantity: 0,
      });
    } catch (error) {
      console.error("Error adding product:", error);
    }
  };

  // Hàm cập nhật sản phẩm
  const handleSaveEdit = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.put(
        `${API_URL}/${editingProduct._id}`,
        editingProduct,
        {
          headers: { "Content-Type": "application/json", Authorization: token },
        }
      );

      setProducts((prevProducts) =>
        prevProducts.map((product) =>
          product._id === editingProduct._id ? response.data : product
        )
      );

      setEditingProduct(null);
    } catch (error) {
      console.error("Error updating product:", error);
    }
  };

  // Hàm xóa sản phẩm
  const handleDeleteProduct = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${API_URL}/${id}`, {
        headers: { Authorization: token },
      });

      setProducts((prevProducts) => prevProducts.filter((product) => product._id !== id));
    } catch (error) {
      console.error("Error deleting product:", error);
    }
  };

  // Hàm để xử lý chỉnh sửa sản phẩm
  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setNewProduct(product);
  };

  return (
    <div className="admin-page">
      <h1>Quản lý sản phẩm</h1>
      <div className="product-form">
        <h2>{editingProduct ? "Chỉnh sửa sản phẩm" : "Thêm sản phẩm"}</h2>
        <input
          type="text"
          placeholder="Tên sản phẩm"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Giá"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
        />
        <input
          type="text"
          placeholder="Mô tả"
          value={newProduct.description}
          onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="URL hình ảnh"
          value={newProduct.imageUrl}
          onChange={(e) => setNewProduct({ ...newProduct, imageUrl: e.target.value })}
        />
        <input
          type="number"
          placeholder="Số lượng"
          value={newProduct.quantity}
          onChange={(e) => setNewProduct({ ...newProduct, quantity: e.target.value })}
        />
        {editingProduct ? (
          <button onClick={handleSaveEdit}>Lưu thay đổi</button>
        ) : (
          <button onClick={handleAddProduct}>Thêm sản phẩm</button>
        )}
      </div>
      <div className="product-list">
        <h2>Danh sách sản phẩm</h2>
        {products.length === 0 ? (
          <p>Không có sản phẩm nào để hiển thị.</p>
        ) : (
          <ul>
            {products.map((product) => (
              <li key={product._id}>
                <h3>{product.name}</h3>
                <p>Giá: {product.price} VND</p>
                <p>Mô tả: {product.description}</p>
                <p>Số lượng: {product.quantity}</p>
                <img src={product.imageUrl} alt={product.name} />
                <button onClick={() => handleEditProduct(product)}>Chỉnh sửa</button>
                <button onClick={() => handleDeleteProduct(product._id)}>Xóa</button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default AdminPage;
