import React, { useState, useEffect } from "react";
import axios from "axios";
import { isAuthenticated, logout } from "../services/AuthService";
import { useNavigate } from "react-router-dom";
import "./AdminPage.css";

const API_URL = "https://orderfoods1.onrender.com/api/orders";

const AdminPage = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    description: "",
    imageUrl: "",
    quantity: 0,
  });
  const [editingProduct, setEditingProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate("/");
      return;
    }

    const fetchProducts = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(API_URL, {
          headers: { Authorization: token },
        });
        console.log("Dữ liệu sản phẩm:", response.data);
        setProducts(response.data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };
    fetchProducts();
  }, [navigate]);

  const isValidProduct = (product) => {
    return (
      product.name &&
      product.price > 0 &&
      product.description &&
      product.imageUrl &&
      product.quantity >= 0
    );
  };

  const handleAddProduct = async () => {
    console.log("New Product:", newProduct);
    if (!isValidProduct(newProduct)) {
      alert("Vui lòng điền đầy đủ thông tin và đảm bảo số lượng là một số hợp lệ");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        API_URL,
        {
          ...newProduct,
          quantity: Number(newProduct.quantity),
        },
        {
          headers: { "Content-Type": "application/json", Authorization: token },
        }
      );
      setProducts([...products, response.data]);
      setNewProduct({
        name: "",
        price: "",
        description: "",
        imageUrl: "",
        quantity: 0,
      });
    } catch (error) {
      console.error("Error adding product:", error);
      alert("Có lỗi xảy ra khi thêm sản phẩm.");
    }
  };

  const handleEditProduct = (product) => {
    setEditingProduct({ ...product });
  };

  const handleSaveEdit = async () => {
    console.log("Editing Product:", editingProduct);
    if (!isValidProduct(editingProduct)) {
      alert("Vui lòng điền đầy đủ thông tin và đảm bảo số lượng là một số hợp lệ");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await axios.put(
        `${API_URL}/${editingProduct._id}`,
        {
          ...editingProduct,
          quantity: Number(editingProduct.quantity),
        },
        {
          headers: { "Content-Type": "application/json", Authorization: token },
        }
      );
      setProducts(
        products.map((product) =>
          product._id === editingProduct._id ? response.data : product
        )
      );
      setEditingProduct(null);
    } catch (error) {
      console.error("Error updating product:", error);
      alert("Có lỗi xảy ra khi cập nhật sản phẩm.");
    }
  };

  const handleDeleteProduct = async (id) => {
    if (window.confirm("Bạn có chắc chắn muốn xóa sản phẩm này không?")) {
      try {
        const token = localStorage.getItem("token");
        await axios.delete(`${API_URL}/${id}`, {
          headers: { Authorization: token },
        });
        setProducts(products.filter((product) => product._id !== id));
      } catch (error) {
        console.error("Error deleting product:", error);
        alert("Có lỗi xảy ra khi xóa sản phẩm.");
      }
    }
  };

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  return (
    <div className="container">
      <h1 className="title">Trang quản trị</h1>
      <button onClick={handleLogout} className="btn btn-danger">
        Đăng xuất
      </button>

      <div className="form-container">
        <h2>{editingProduct ? "Sửa sản phẩm" : "Thêm sản phẩm"}</h2>
        <div className="form-group">
          <input
            type="text"
            className="form-control"
            placeholder="Tên sản phẩm"
            value={editingProduct ? editingProduct.name : newProduct.name}
            onChange={(e) =>
              editingProduct
                ? setEditingProduct({ ...editingProduct, name: e.target.value })
                : setNewProduct({ ...newProduct, name: e.target.value })
            }
          />
          <input
            type="number"
            className="form-control"
            placeholder="Giá"
            value={editingProduct ? editingProduct.price : newProduct.price}
            onChange={(e) =>
              editingProduct
                ? setEditingProduct({ ...editingProduct, price: e.target.value })
                : setNewProduct({ ...newProduct, price: e.target.value })
            }
          />
          <input
            type="text"
            className="form-control"
            placeholder="Mô tả"
            value={editingProduct ? editingProduct.description : newProduct.description}
            onChange={(e) =>
              editingProduct
                ? setEditingProduct({ ...editingProduct, description: e.target.value })
                : setNewProduct({ ...newProduct, description: e.target.value })
            }
          />
          <input
            type="text"
            className="form-control"
            placeholder="URL Hình Ảnh"
            value={editingProduct ? editingProduct.imageUrl : newProduct.imageUrl}
            onChange={(e) =>
              editingProduct
                ? setEditingProduct({ ...editingProduct, imageUrl: e.target.value })
                : setNewProduct({ ...newProduct, imageUrl: e.target.value })
            }
          />
          <input
            type="number"
            className="form-control"
            placeholder="Số lượng"
            value={editingProduct ? editingProduct.quantity : newProduct.quantity}
            onChange={(e) =>
              editingProduct
                ? setEditingProduct({ ...editingProduct, quantity: e.target.value })
                : setNewProduct({ ...newProduct, quantity: e.target.value })
            }
          />
        </div>
        <button
          className="btn btn-primary"
          onClick={editingProduct ? handleSaveEdit : handleAddProduct}
        >
          {editingProduct ? "Lưu thay đổi" : "Thêm sản phẩm"}
        </button>
      </div>

      <div className="product-list">
        <h2>Danh sách sản phẩm</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Tên sản phẩm</th>
              <th>Giá</th>
              <th>Mô tả</th>
              <th>Hình ảnh</th>
              <th>Hành động</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product._id}>
                <td>{product.name}</td>
                <td>{product.price}</td>
                <td>{product.description}</td>
                <td>
                  <img src={product.imageUrl} alt={product.name} className="product-image" />
                </td>
                <td>
                  <button onClick={() => handleEditProduct(product)}>Sửa</button>
                  <button onClick={() => handleDeleteProduct(product._id)}>Xóa</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminPage;
